static int run_all_chacha_tests(struct chacha_test_data_t *data)
{
    int rc = 0;

    rc |= chacha_test_lowlevel_oneshot_encrypt(data);
    rc |= chacha_test_lowlevel_oneshot_decrypt(data);
    rc |= chacha_test_lowlevel_encrypt_decrypt(data);
    rc |= chacha_test_lowlevel_verify_negative(data);
    rc |= chacha_test_lowlevel_multipart_encrypt(data, 16);
    rc |= chacha_test_lowlevel_multipart_decrypt(data, 16);
    rc |= chacha_test_lowlevel_multipart_encrypt(data, 7);
    rc |= chacha_test_lowlevel_multipart_decrypt(data, 3);
    rc |= chacha_test_lowlevel_multipart_saveload_encrypt(data, 16);
    rc |= chacha_test_lowlevel_multipart_saveload_decrypt(data, 16);
    rc |= chacha_test_lowlevel_multipart_saveload_encrypt(data, 3);
    rc |= chacha_test_lowlevel_multipart_saveload_decrypt(data, 3);
    rc |= chacha_test_lowlevel_oneshot_inplace_encrypt(data);
    rc |= chacha_test_lowlevel_oneshot_inplace_decrypt(data);

    return rc;
}

static void chacha_lowlevel_tests_run(struct test_result_t *ret)
{
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_ietf_example) == 0,
                "chacha_test_ietf_example should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_modified_ietf_example) == 0,
                "chacha_test_modified_ietf_example should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_ietf_vector) == 0,
                "chacha_test_ietf_vector should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_block_with_auth_data) == 0,
                "chacha_test_block_with_auth_data should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_multiblock) == 0,
                "chacha_test_multiblock should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_auth_data_only) == 0,
                "chacha_test_auth_data_only should pass");
    TEST_ASSERT(run_all_chacha_tests(&chacha_test_short_auth_data_only) == 0,
                "chacha_test_short_auth_data_only should pass");
}

static struct test_t chacha_tests = {
    &chacha_lowlevel_tests_run,
     "CC3XX_CHACHA_TEST",
    "CC3XX chacha tests",
};

void add_cc3xx_chacha_tests_to_testsuite(struct test_suite_t *p_ts, uint32_t ts_size)
{
#if defined(CC3XX_CONFIG_CHACHA_ENABLE) && defined(CC3XX_CONFIG_CHACHA_POLY1305_ENABLE)
    cc3xx_add_tests_to_testsuite(&chacha_tests, 1, p_ts, ts_size);
#endif /* defined(CC3XX_CONFIG_CHACHA_ENABLE) && defined(CC3XX_CONFIG_CHACHA_POLY1305_ENABLE) */
}
